import React from "react";
import { useState, useCallback , useEffect } from "react";
import { Menu } from "./Home.js";
import "./ContactUs.css";
import Axios from "axios";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import InputGroup from "react-bootstrap/InputGroup";
import FormControl from "react-bootstrap/FormControl";
import "react-datepicker/dist/react-datepicker.css";
import DatePicker from "react-datepicker";

const ContactUs = () => {
  const [startDate, setStartDate] = useState(new Date());
  const [endDate, setEndDate] = useState(new Date());
  const [isSingleEvent, setSingleEvent] = useState(false);
  let [region, setRegion] = useState("Choose State");
  let [zip, setZip] = useState("");
  let [otherEvent, setOtherEvent] = useState("");
  let [selectedValue , setSelectedValue] = useState("");
  const [formData, setFormData] = useState({
    fName: "",
    lName: "",
    username: "",
    events: [
      {
        eventType: "Single Event",
        eventName: {otherEvent},
        eventStartDate: { startDate },
        eventEndDate: { endDate },
        country: "India",
        state: { region },
        region: { zip },
      },
    ],
    eventCategory: "",
  });

  const inputHandler = useCallback((e) => {
    setFormData((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }));
  }, []);

  // const subArrayObjects = useCallback((e) => {
  //   setFormData({
  //     events: formData.events.map((prevState) => ({
  //       ...prevState,
  //       [e.target.name]: e.target.value,
  //     })),
  //   });
  // }, []);

  

  const selectHandler = (e) => {
    console.log("Previous Value "+selectedValue);
    setSelectedValue(prevSelectedValue => prevSelectedValue = e.target.value);
    console.log("Updated Value "+selectedValue);
    selectedValue === 'Other'
       ?setSingleEvent(prevIsSingleEvent => prevIsSingleEvent = true)
      :setSingleEvent(prevIsSingleEvent => prevIsSingleEvent = false);
    console.log(isSingleEvent);
    setFormData({
      events: formData.events.map((prevState) => ({
        ...prevState,
        eventName: e.target.value,
      })),
    },[selectedValue, isSingleEvent ]);
  }

  const contactUsHandler = (event) => {
    event.preventDefault();
    Object.entries(formData).map((item) => {
      console.log(item);
    });
  };

  //const [multipleEvent , setMultipleEvent] = useState('Multiple Event');
  const eventOptions = [
    { value: "Wedding" },
    { value: "Pre-wedding" },
    { value: "Maternity" },
    { value: "Baby Shower" },
    { value: "Product Photoshoot" },
    { value: "Event Photos" },
    { value: "Fashion Photography" },
    { value: "Other" },
  ];

  const stateOptions = [
    "Andhra Pradesh",
    "Arunachal Pradesh",
    "Assam",
    "Bihar",
    "Chhattisgarh",
    "Goa",
    "Gujarat",
    "Haryana",
    "Himachal Pradesh",
    "Jammu and Kashmir",
    "Jharkhand",
    "Karnataka",
    "Kerala",
    "Madhya Pradesh",
    "Maharashtra",
    "Manipur",
    "Meghalaya",
    "Mizoram",
    "Nagaland",
    "Odisha",
    "Punjab",
    "Rajasthan",
    "Sikkim",
    "Tamil Nadu",
    "Telangana",
    "Tripura",
    "Uttar Pradesh",
    "Uttarakhand",
    "West Bengal",
  ];

  const fetchZipCode = (e) => {
    setZip(e.target.value);
    let newData = [{}];
    if (e.target.value.length === 6) {
      Axios.get(`http://localhost:5000/region/${e.target.value}`)
        .then((response) => {
          for (let i = 0; i < response.data.length; i++) {
            newData = response.data.map((res) => ({
              id: res.id,
              message: res.message,
              status: res.status,
              postOffice: [
                {
                  Name: res.Name,
                  Description: res.Description,
                  BranchType: res.BranchType,
                  DeliveryStatus: res.DeliveryStatus,
                  Taluk: res.Taluk,
                  Circle: res.Circle,
                  District: res.District,
                  Division: res.Division,
                  Region: res.Region,
                  State: res.State,
                  Country: res.Country,
                },
              ],
            }));
          }

          const state = newData.find((el) => el.id == "277201");
          setRegion(state.status);
        })
        .catch(function (error) {
          // handle error
          console.log(error);
        })
        .then(function () {
          // always executed
        });
    } else {
    }
  };

  
  return (
    <div className="bg">
      <Menu />
      <br />
      <div className="offset-md-3 col-md-6 container card ">
        <Form className="mt-2" onSubmit={contactUsHandler}>
          <Form.Group controlId="formGroupFName">
            <Form.Label>First Name</Form.Label>
            <Form.Control
              type="text"
              placeholder="First Name"
              name="fName"
              value={formData.fName}
              onChange={inputHandler}
            />
          </Form.Group>
          <Form.Group controlId="formGroupLName">
            <Form.Label>Last Name</Form.Label>
            <Form.Control
              name="lName"
              type="text"
              placeholder="Last Name"
              onChange={inputHandler}
            />
          </Form.Group>
          <Form.Group controlId="formGroupEmail">
            <Form.Label>Email address</Form.Label>
            <InputGroup>
              <InputGroup.Prepend>
                <InputGroup.Text>@</InputGroup.Text>
              </InputGroup.Prepend>
              &nbsp;
              <FormControl
                name="username"
                type="email"
                placeholder="Username"
                onChange={inputHandler}
              />
            </InputGroup>
          </Form.Group>

          <Form.Group controlId="formGroupEvent">
            <Form.Label>Event:</Form.Label>

            {["radio"].map((type) => (
              <div key={`inline-${type}`} className="mb-3">
                <Form.Check
                  inline
                  label="Single Event"
                  type={type}
                  id={`inline-${type}-1`}
                  name="eventType"
                  defaultChecked
                  onClick={
                    () => {
                      setFormData({
                        events: formData.events.map((prevState) => ({
                          ...prevState,
                          eventType: "Single Event",
                        })),
                      });
                    }
                  }
                />
                <Form.Check
                  inline
                  label="Multiple Event"
                  type={type}
                  id={`inline-${type}-2`}
                  name="eventType"
                  onClick={ () => {
                    setFormData({
                      events: formData.events.map((prevState) => ({
                        ...prevState,
                        eventType: "Multiple Event",
                      })),
                    });
                  }}
                />
              </div>
            ))}

            <div className="row">
              <div className="col-md-5">
                <Form.Control
                  as="select"
                  defaultValue="Select Event"
                  name="eventName"
                  // onChange={subArrayObjects}
                  onChange={selectHandler}
                >
                  <option disabled>Select Event</option>
                  {eventOptions.map((option, index) => {
                    return (
                      <option key={index} value={option.value}>
                        {option.value}
                      </option>
                    );
                  })}
                </Form.Control>
              </div>
              <div className="col-md-7">
                <FormControl
                  disabled={isSingleEvent === false}
                  name="eventName"
                  onChange={useCallback((e) => {
                    console.log(isSingleEvent);
                    if(isSingleEvent === true){
                    setOtherEvent(e.target.value);
                    setFormData({
                      events: formData.events.map((prevState) => ({
                        ...prevState,
                        eventName: otherEvent,
                      })),
                    });
                  }
                  else{
                    setOtherEvent(" ");
                  } 
                  }, [])}
                  value = {otherEvent}
                  placeholder="Other Event "
                />
              </div>
            </div>
          </Form.Group>
          <div className="row">
            <div className="col-md-6 ">
              <Form.Group controlId="formGroupDate">
                <Form.Label>Event Start Date &nbsp;</Form.Label>
                <DatePicker
                  required
                  className="text-muted"
                  closeOnScroll={true}
                  closeOnScroll={(e) => e.target === document}
                  selected={startDate}
                  onChange={useCallback((date) => {
                    setStartDate(date);
                    setFormData({
                      events: formData.events.map((prevState) => ({
                        ...prevState,
                        eventStartDate: date,
                      })),
                    });
                  }, [])}
                />
              </Form.Group>
            </div>
            <div className="col-md-6 ">
              <Form.Group controlId="formGroupDate">
                <Form.Label>Event End Date &nbsp;</Form.Label>
                <DatePicker
                required
                  className="text-muted"
                  closeOnScroll={true}
                  closeOnScroll={(e) => e.target === document}
                  selected={endDate}
                  onChange={useCallback((date) => {
                    setEndDate(date);
                    setFormData({
                      events: formData.events.map((prevState) => ({
                        ...prevState,
                        eventEndDate: date,
                      })),
                    });
                  }, [])}
                />
              </Form.Group>
            </div>
          </div>
          <Form.Group>
            <div className="row">
              <div className="col">
                <Form.Control as="select" defaultValue="India">
                  <option disabled>Choose Country</option>
                  <option>India</option>
                </Form.Control>
                &nbsp;
              </div>
              <div className="col">
                <Form.Control
                  as="select"
                  value={region}
                  onChange={(e) => {
                    setRegion(e.target.value);
                    if (zip) {
                      setZip("");
                    }
                  }}
                >
                  <option disabled>Choose State</option>
                  {stateOptions.map((option, index) => {
                    return (
                      <option key={index} value={option}>
                        {option}
                      </option>
                    );
                  })}
                </Form.Control>
              </div>
              <div className="col">
                <Form.Control
                  type="number"
                  placeholder="Zip Code"
                  onChange={fetchZipCode}
                  value={zip}
                />
              </div>
            </div>
          </Form.Group>
          <Form.Group>
            <Form.Control
              as="select"
              name = "eventCategory"
              onChange = {inputHandler}
            >
              <option disabled>Event Style</option>
              <option>Classic</option>
              <option>Industrial</option>
              <option>Vintage</option>
              <option>Miscellaneous</option>
            </Form.Control>
          </Form.Group>
          <Form.Group>
            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Form.Group>
        </Form>
      </div>
    </div>
  );
};

export default ContactUs;
