import React from "react";
import { useState , useEffect} from "react";
import { Menu } from "./Home.js";
import Axios from "axios";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import InputGroup from "react-bootstrap/InputGroup";
import FormControl from "react-bootstrap/FormControl";
import "react-datepicker/dist/react-datepicker.css";
import DatePicker from "react-datepicker";
import { IoConstruct } from "react-icons/io5";

const ContactUs = () => {
  const [startDate, setStartDate] = useState(new Date());
  const [endDate, setEndDate] = useState(new Date());
  const eventOptions = [
    { value: "Wedding" },
    { value: "Pre-wedding" },
    { value: "Maternity" },
    { value: "Baby Shower" },
    { value: "Product Photoshoot" },
    { value: "Event Photos" },
    { value: "Fashion Photography" },
    { value: "Other" },
  ];

  const stateOptions = [
    "Andhra Pradesh",
    "Arunachal Pradesh",
    "Assam",
    "Bihar",
    "Chhattisgarh",
    "Goa",
    "Gujarat",
    "Haryana",
    "Himachal Pradesh",
    "Jammu and Kashmir",
    "Jharkhand",
    "Karnataka",
    "Kerala",
    "Madhya Pradesh",
    "Maharashtra",
    "Manipur",
    "Meghalaya",
    "Mizoram",
    "Nagaland",
    "Odisha",
    "Punjab",
    "Rajasthan",
    "Sikkim",
    "Tamil Nadu",
    "Telangana",
    "Tripura",
    "Uttar Pradesh",
    "Uttarakhand",
    "West Bengal",
  ];

  let [zip, setRegion] = useState([{}]);

  // useEffect(() => {
  //   fetchZipCode()
  // });
  
  const fetchZipCode = (e) => {
    zip = e.target.value;
    Axios.get(`http://localhost:5000/users/`)
    .then( (response) => {
     console.log( response.data[0].username);
     setRegion(zip);
     console.log(zip);
    })
    .catch(function (error) {
      // handle error
      console.log(error);
    })
    .then(function () {
      // always executed
    });
  }

  const contactUsHandler = event => {
    event.preventDefault();
    alert("Form Submitted");
  }

  // const ExampleCustomInput = forwardRef(
  //   ({ value, onClick }, ref) => (
  //     <button className="example-custom-input" onClick={onClick} ref={ref}>
  //       {value}
  //     </button>
  //   ),
  // );
  return (
    <div>
      <Menu />
      <br />
      <div className="offset-md-3 col-md-6 container card bg ">
        <Form className="mt-2" onSubmit = {contactUsHandler}>
          <Form.Group controlId="formGroupFName">
            <Form.Label>First Name</Form.Label>
            <Form.Control type="text" placeholder="First Name" />
          </Form.Group>
          <Form.Group controlId="formGroupLName">
            <Form.Label>Last Name</Form.Label>
            <Form.Control type="text" placeholder="Last Name" />
          </Form.Group>
          <Form.Group controlId="formGroupEmail">
            <Form.Label>Email address</Form.Label>
            <InputGroup>
              <InputGroup.Prepend>
                <InputGroup.Text>@</InputGroup.Text>
              </InputGroup.Prepend>
              &nbsp;
              <FormControl
                // id="inlineFormInputGroupUsername"
                placeholder="Username"
              />
            </InputGroup>
          </Form.Group>

          <Form.Group controlId="formGroupEvent">
            <Form.Label>Event:</Form.Label>

            {["radio"].map((type) => (
              <div key={`inline-${type}`} className="mb-3">
                <Form.Check
                  inline
                  label="Single Event"
                  type={type}
                  id={`inline-${type}-1`}
                  name="eventTypeGrp"
                />
                <Form.Check
                  inline
                  label="Multiple Event"
                  type={type}
                  id={`inline-${type}-2`}
                  name="eventTypeGrp"
                />
              </div>
            ))}

            <div className="row">
              <div className="col-md-5">
                <Form.Control as="select" defaultValue="Select Event">
                  <option disabled>Select Event</option>
                  {eventOptions.map((option, index) => {
                    return (
                      <option key={index} value={option.value}>
                        {option.value}
                      </option>
                    );
                  })}
                </Form.Control>
              </div>
              <div className="col-md-7">
                <FormControl
                  // id="inlineFormInputGroupUsername"
                  placeholder="Other Event "
                />
              </div>
            </div>
          </Form.Group>
          <div className="row">
            <div className="col-md-6 ">
              <Form.Group controlId="formGroupDate">
                <Form.Label>Event Start Date &nbsp;</Form.Label>
                <DatePicker
                  className="text-muted"
                  // isClearable
                  closeOnScroll={true}
                  closeOnScroll={(e) => e.target === document}
                  // customInput={<ExampleCustomInput />}
                  selected={startDate}
                  onChange={(date) => setStartDate(date)}
                />
              </Form.Group>
            </div>
            <div className="col-md-6 ">
              <Form.Group controlId="formGroupDate">
                <Form.Label>Event End Date &nbsp;</Form.Label>
                <DatePicker
                  className="text-muted"
                  // isClearable
                  closeOnScroll={true}
                  closeOnScroll={(e) => e.target === document}
                  // customInput={<ExampleCustomInput />}
                  selected={endDate}
                  onChange={(date) => setEndDate(date)}
                />
              </Form.Group>
            </div>
          </div>
          <Form.Group>
            <div className="row">
              <div className="col">
                <Form.Control as="select" defaultValue="Choose Country">
                  <option disabled>Choose Country</option>
                  <option>India</option>
                </Form.Control>
                &nbsp;
              </div>
              <div className="col">
                <Form.Control as="select" defaultValue="Choose State"  > 
                  <option disabled>Choose State</option>
                  {stateOptions.map((option, index) => {
                    return (
                      <option key={index} value={option}>
                        {option}
                      </option>
                    );
                  })}
                </Form.Control>
              </div>
              <div className="col">
                <Form.Control type="text" placeholder="Zip Code"   onChange = {(e) => setRegion(e.target.value)} />
              </div>
            </div>
          </Form.Group>
          <Form.Group>
            <Form.Control as="select">
              <option>Event Style</option>
              <option>Classic</option>
              <option>Industrial</option>
              <option>Vintage</option>
              <option>Miscellaneous</option>
            </Form.Control>
          </Form.Group>
          <Form.Group>
            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Form.Group>
        </Form>
      </div>
    </div>
  );
};

export default ContactUs;
