import React from "react";
import { useState } from "react";
import { Menu } from "./Home.js";
import Axios from "axios";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import InputGroup from "react-bootstrap/InputGroup";
import FormControl from "react-bootstrap/FormControl";
import "react-datepicker/dist/react-datepicker.css";
import DatePicker, { registerLocale } from "react-datepicker";
import { IndexLinkContainer } from "react-router-bootstrap";

const ContactUs = () => {
  const [startDate, setStartDate] = useState(new Date());
  const [endDate, setEndDate] = useState(new Date());
  const options = [{ value: "Select Event" },
   { value: "Wedding" },
   { value: "Pre-wedding" },
   { value: "Maternity" },
   { value: "Baby Shower" },
   { value: "Wedding" },
   { value: "Wedding" },
   { value: "Wedding" }];

  // const ExampleCustomInput = forwardRef(
  //   ({ value, onClick }, ref) => (
  //     <button className="example-custom-input" onClick={onClick} ref={ref}>
  //       {value}
  //     </button>
  //   ),
  // );
  return (
    <div>
      <Menu />
      <br />
      <div className="offset-md-3 col-md-6 container card ">
        <Form className="mt-2">
          <Form.Group controlId="formGroupFName">
            <Form.Label>First Name</Form.Label>
            <Form.Control type="text" placeholder="First Name" />
          </Form.Group>
          <Form.Group controlId="formGroupLName">
            <Form.Label>Last Name</Form.Label>
            <Form.Control type="text" placeholder="Last Name" />
          </Form.Group>
          <Form.Group controlId="formGroupEmail">
            <Form.Label>Email address</Form.Label>
            <InputGroup>
              <InputGroup.Prepend>
                <InputGroup.Text>@</InputGroup.Text>
              </InputGroup.Prepend>
              &nbsp;
              <FormControl
                id="inlineFormInputGroupUsername"
                placeholder="Username"
              />
            </InputGroup>
          </Form.Group>

          <Form.Group controlId="formGroupEvent">
            <Form.Label>Event:</Form.Label>

            {["radio"].map((type) => (
              <div key={`inline-${type}`} className="mb-3">
                <Form.Check
                  inline
                  label="Single Event"
                  type={type}
                  id={`inline-${type}-1`}
                />
                <Form.Check
                  inline
                  label="Multiple Event"
                  type={type}
                  id={`inline-${type}-2`}
                />
              </div>
            ))}

            <div className="row">
              <div className="col-md-4">
              <Form.Control as="select">
                  {
                    options.map((option,index) => {
                      return(
                        <option key = {index} value = {option.value} disabled = {index===0} >
                          {option.value}
                          {console.log(index,option.value)}
                          </option>
                      );
                    })
                  }
                </Form.Control>
                &nbsp;
              </div>
              <div className="col-md-8">
                <FormControl
                  id="inlineFormInputGroupUsername"
                  placeholder="Enter Event "
                />
              </div>
            </div>
          </Form.Group>
          <div className="row">
            <div className="col-md-6 ">
              <Form.Group controlId="formGroupDate">
                <Form.Label>Event Start Date &nbsp;</Form.Label>
                <DatePicker
                  className="text-muted"
                  locale="es"
                  // isClearable
                  closeOnScroll={true}
                  closeOnScroll={(e) => e.target === document}
                  // customInput={<ExampleCustomInput />}
                  selected={startDate}
                  onChange={(date) => setStartDate(date)}
                />
              </Form.Group>
            </div>
            <div className="col-md-6 ">
              <Form.Group controlId="formGroupDate">
                <Form.Label>Event End Date &nbsp;</Form.Label>
                <DatePicker
                  className="text-muted"
                  locale="es"
                  // isClearable
                  closeOnScroll={true}
                  closeOnScroll={(e) => e.target === document}
                  // customInput={<ExampleCustomInput />}
                  selected={endDate}
                  onChange={(date) => setEndDate(date)}
                />
              </Form.Group>
            </div>
          </div>
          <Form.Group>
            <div className="row">
              <div className="col">
                <Form.Control as="select">
                  <option>Choose Country</option>
                  <option>2</option>
                  <option>3</option>
                  <option>4</option>
                  <option>5</option>
                </Form.Control>
                &nbsp;
              </div>
              <div className="col">
                <Form.Control as="select">
                  <option>Choose State</option>
                  <option>2</option>
                  <option>3</option>
                  <option>4</option>
                  <option>5</option>
                </Form.Control>
              </div>
              <div className="col">
                <Form.Control type="text" placeholder="Zip Code" />
              </div>
            </div>
          </Form.Group>
          <Form.Group>
            <Form.Control as="select">
              <option>Event Style</option>
              <option>Classic</option>
              <option>Industrial</option>
              <option>Vintage</option>
              <option>Miscellaneous</option>
            </Form.Control>
          </Form.Group>
          <Form.Group>
            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Form.Group>
        </Form>
      </div>
    </div>
  );
};

export default ContactUs;
