import React from "react";
import { useState, useCallback , useEffect } from "react";
import { Menu } from "./Home.js";
import "./ContactUs.css";
import Axios from "axios";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import InputGroup from "react-bootstrap/InputGroup";
import FormControl from "react-bootstrap/FormControl";
import "react-datepicker/dist/react-datepicker.css";
import DatePicker from "react-datepicker";

const ContactUs = () => {
  const [startDate, setStartDate] = useState(new Date());
  const [endDate, setEndDate] = useState(new Date());
  const [isSingleEvent, setSingleEvent] = useState(false);
  const [region, setRegion] = useState("Choose State");
  const [OtherEventName , setOtherEventName] = useState("");
  const [zip, setZip] = useState("");
  const initialFormState = 
  {
    userId: 101,
    fName: "",
    lName: "",
    username: "",
    events: [
      {
        eventType: "Single Event",
        eventName: "",
        eventStartDate: { startDate },
        eventEndDate: { endDate },
        country: "India",
        state: { region },
        region: { zip },
      },
    ],
    eventCategory: "",
  }

  const [formData, setFormData] = useState(initialFormState);

  
  const inputHandler = (e) => {
    setFormData((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }));
  }

  const subArrayObjects = (e) => {
    setFormData({
      events: formData.events.map((prevState) => ({
        ...prevState,
        [e.target.name]: e.target.value,
      })),
    });
    setOtherEventName( e.target.value );
  }

  const regionSelectHandler =(e) => {
    selectHandler(e);
    setRegion(e.target.value);
    console.log(zip);
    if (zip) {
      setZip("");
    }
  }

  const eventSelectHandler = (e) => {
    e.target.value === 'Other'
       ?setSingleEvent(true)
      :setSingleEvent(false);
    selectHandler(e);
    setOtherEventName("");
  }




  const selectHandler = (e) => {
    setFormData({
      events: formData.events.map((prevState) => ({
        ...prevState,
        [e.target.name]: e.target.value,
      }))
    })

  }

  const contactUsHandler = (event) => {
    Axios.post('http://localhost:5000/users/add', {
      formData
    })
    .then(function (response) {
      console.log(response);
    })
    .catch(function (error) {
      console.log(error);
    });
  };

  //const [multipleEvent , setMultipleEvent] = useState('Multiple Event');
  const eventOptions = [
    { value: "Wedding" },
    { value: "Pre-wedding" },
    { value: "Maternity" },
    { value: "Baby Shower" },
    { value: "Product Photoshoot" },
    { value: "Event Photos" },
    { value: "Fashion Photography" },
    { value: "Other" },
  ];

  const stateOptions = [
    "Andhra Pradesh",
    "Arunachal Pradesh",
    "Assam",
    "Bihar",
    "Chhattisgarh",
    "Goa",
    "Gujarat",
    "Haryana",
    "Himachal Pradesh",
    "Jammu and Kashmir",
    "Jharkhand",
    "Karnataka",
    "Kerala",
    "Madhya Pradesh",
    "Maharashtra",
    "Manipur",
    "Meghalaya",
    "Mizoram",
    "Nagaland",
    "Odisha",
    "Punjab",
    "Rajasthan",
    "Sikkim",
    "Tamil Nadu",
    "Telangana",
    "Tripura",
    "Uttar Pradesh",
    "Uttarakhand",
    "West Bengal",
  ];

  const fetchZipCode = (e) => {
    setZip(e.target.value);
    let newData = [{}];
    if (e.target.value.length === 6) {
      Axios.get(`http://localhost:5000/region/${e.target.value}`)
        .then((response) => {
          for (let i = 0; i < response.data.length; i++) {
            newData = response.data.map((res) => ({
              id: res.id,
              message: res.message,
              status: res.status,
              postOffice: [
                {
                  Name: res.Name,
                  Description: res.Description,
                  BranchType: res.BranchType,
                  DeliveryStatus: res.DeliveryStatus,
                  Taluk: res.Taluk,
                  Circle: res.Circle,
                  District: res.District,
                  Division: res.Division,
                  Region: res.Region,
                  State: res.State,
                  Country: res.Country,
                },
              ],
            }));
          }

          const state = newData.find((el) => el.id == "277201");
          setRegion(state.status);
        })
        .catch(function (error) {
          // handle error
          console.log(error);
        })
        .then(function () {
          // always executed
        });
    } else {
    }
  };

  
  return (
    <div className="bg">
      <Menu />
      <br />
      <div className="offset-md-3 col-md-6 container card ">
        <Form className="mt-2" onSubmit={contactUsHandler}>
          <Form.Group controlId="formGroupFName">
            <Form.Label>First Name</Form.Label>
            <Form.Control
              type="text"
              placeholder="First Name"
              name="fName"
              value={formData.fName}
              onChange={inputHandler}
            />
          </Form.Group>
          <Form.Group controlId="formGroupLName">
            <Form.Label>Last Name</Form.Label>
            <Form.Control
              name="lName"
              type="text"
              placeholder="Last Name"
              onChange={inputHandler}
            />
          </Form.Group>
          <Form.Group controlId="formGroupEmail">
            <Form.Label>Email address</Form.Label>
            <InputGroup>
              <InputGroup.Prepend>
                <InputGroup.Text>@</InputGroup.Text>
              </InputGroup.Prepend>
              &nbsp;
              <FormControl
                name="username"
                type="email"
                placeholder="Username"
                onChange={inputHandler}
              />
            </InputGroup>
          </Form.Group>

          <Form.Group controlId="formGroupEvent">
            <Form.Label>Event:</Form.Label>

            {["radio"].map((type) => (
              <div key={`inline-${type}`} className="mb-3">
                <Form.Check
                  inline
                  label="Single Event"
                  type={type}
                  id={`inline-${type}-1`}
                  name="eventType"
                  defaultChecked
                  onClick={
                    () => {
                      setFormData({
                        events: formData.events.map((prevState) => ({
                          ...prevState,
                          eventType: "Single Event",
                        })),
                      });
                    }
                  }
                />
                <Form.Check
                  inline
                  label="Multiple Event"
                  type={type}
                  id={`inline-${type}-2`}
                  name="eventType"
                  onClick={ () => {
                    setFormData({
                      events: formData.events.map((prevState) => ({
                        ...prevState,
                        eventType: "Multiple Event",
                      })),
                    });
                  }}
                />
              </div>
            ))}

            <div className="row">
              <div className="col-md-5">
                <Form.Control
                  as="select"
                  defaultValue="Select Event"
                  name="eventName"
                  onChange={eventSelectHandler}
                >
                  <option disabled>Select Event</option>
                  {eventOptions.map((option, index) => {
                    return (
                      <option key={index} value={option.value}>
                        {option.value}
                      </option>
                    );
                  })}
                </Form.Control>
              </div>
              <div className="col-md-7">
                <FormControl
                  disabled={isSingleEvent === false}
                  name="eventName"
                  onChange={subArrayObjects}
                  placeholder="Other Event "
                  value = {OtherEventName}
                />
              </div>
            </div>
          </Form.Group>
          <div className="row">
            <div className="col-md-6 ">
              <Form.Group controlId="formGroupDate">
                <Form.Label>Event Start Date &nbsp;</Form.Label>
                <DatePicker
                  required
                  className="text-muted"
                  closeOnScroll={true}
                  closeOnScroll={(e) => e.target === document}
                  selected={startDate}
                  onChange={useCallback((date) => {
                    setStartDate(date);
                    setFormData({
                      events: formData.events.map((prevState) => ({
                        ...prevState,
                        eventStartDate: date,
                      })),
                    });
                  }, [])}
                />
              </Form.Group>
            </div>
            <div className="col-md-6 ">
              <Form.Group controlId="formGroupDate">
                <Form.Label>Event End Date &nbsp;</Form.Label>
                <DatePicker
                required
                  className="text-muted"
                  closeOnScroll={true}
                  closeOnScroll={(e) => e.target === document}
                  selected={endDate}
                  onChange={useCallback((date) => {
                    setEndDate(date);
                    setFormData({
                      events: formData.events.map((prevState) => ({
                        ...prevState,
                        eventEndDate: date,
                      })),
                    });
                  }, [])}
                />
              </Form.Group>
            </div>
          </div>
          <Form.Group>
            <div className="row">
              <div className="col">
                <Form.Control as="select" defaultValue="India" name = "country">
                  <option disabled>Choose Country</option>
                  <option>India</option>
                </Form.Control>
                &nbsp;
              </div>
              <div className="col">
                <Form.Control
                  as="select"
                  value={region}
                  name = "state"
                  onChange={regionSelectHandler}
                >
                  <option disabled>Choose State</option>
                  {stateOptions.map((option, index) => {
                    return (
                      <option key={index} value={option}>
                        {option}
                      </option>
                    );
                  })}
                </Form.Control>
              </div>
              <div className="col">
                <Form.Control
                  type="number"
                  placeholder="Zip Code"
                  name = "region"
                  onChange={fetchZipCode}
                  value={zip}
                />
              </div>
            </div>
          </Form.Group>
          <Form.Group>
            <Form.Control
              as="select"
              name = "eventCategory"
              onChange = {inputHandler}
            >
              <option disabled>Event Style</option>
              <option>Classic</option>
              <option>Industrial</option>
              <option>Vintage</option>
              <option>Miscellaneous</option>
            </Form.Control>
          </Form.Group>
          <Form.Group>
            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Form.Group>
        </Form>
      </div>
    </div>
  );
};

export default ContactUs;
