import React from "react";
import { useState } from "react";
import { Menu } from "./Home.js";
import "./ContactUs.css";
import Axios from "axios";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import InputGroup from "react-bootstrap/InputGroup";
import FormControl from "react-bootstrap/FormControl";
import "react-datepicker/dist/react-datepicker.css";
import DatePicker from "react-datepicker";

const ContactUs = () => {
  const [startDate, setStartDate] = useState(new Date());
  const [endDate, setEndDate] = useState(new Date());
  const [isSingleEvent, setSingleEvent] = useState(false);
  //const [multipleEvent , setMultipleEvent] = useState('Multiple Event');
  const eventOptions = [
    { value: "Wedding" },
    { value: "Pre-wedding" },
    { value: "Maternity" },
    { value: "Baby Shower" },
    { value: "Product Photoshoot" },
    { value: "Event Photos" },
    { value: "Fashion Photography" },
    { value: "Other" },
  ];

  const stateOptions = [
    "Andhra Pradesh",
    "Arunachal Pradesh",
    "Assam",
    "Bihar",
    "Chhattisgarh",
    "Goa",
    "Gujarat",
    "Haryana",
    "Himachal Pradesh",
    "Jammu and Kashmir",
    "Jharkhand",
    "Karnataka",
    "Kerala",
    "Madhya Pradesh",
    "Maharashtra",
    "Manipur",
    "Meghalaya",
    "Mizoram",
    "Nagaland",
    "Odisha",
    "Punjab",
    "Rajasthan",
    "Sikkim",
    "Tamil Nadu",
    "Telangana",
    "Tripura",
    "Uttar Pradesh",
    "Uttarakhand",
    "West Bengal",
  ];

  let [region, setRegion] = useState("Choose State");
  let [isStateSelected, setStateSelected] = useState(false);
  let [zip, setZip] = useState(null);

  const fetchZipCode = (e) => {
    setZip = e.target.value;
    let newData = [{}];
    if (e.target.value.length === 6) {
      Axios.get(`http://localhost:5000/region/${e.target.value}`)
        .then((response) => {
          for (let i = 0; i < response.data.length; i++) {
            newData = response.data.map((res) => ({
              id: res.id,
              message: res.message,
              status: res.status,
              postOffice: [
                {
                  Name: res.Name,
                  Description: res.Description,
                  BranchType: res.BranchType,
                  DeliveryStatus: res.DeliveryStatus,
                  Taluk: res.Taluk,
                  Circle: res.Circle,
                  District: res.District,
                  Division: res.Division,
                  Region: res.Region,
                  State: res.State,
                  Country: res.Country,
                },
              ],
            }));
          }

          const state = newData.find((el) => el.id == "277201");
          setRegion(state.status);
        })
        .catch(function (error) {
          // handle error
          console.log(error);
        })
        .then(function () {
          // always executed
        });
    } else {
    }
  };

  const contactUsHandler = (event) => {
    event.preventDefault();
    alert("Form Submitted");
  };

  // const ExampleCustomInput = forwardRef(
  //   ({ value, onClick }, ref) => (
  //     <button className="example-custom-input" onClick={onClick} ref={ref}>
  //       {value}
  //     </button>
  //   ),
  // );
  return (
    <div className="bg">
      <Menu />
      <br />
      <div className="offset-md-3 col-md-6 container card ">
        <Form className="mt-2" onSubmit={contactUsHandler}>
          <Form.Group controlId="formGroupFName">
            <Form.Label>First Name</Form.Label>
            <Form.Control type="text" placeholder="First Name" />
          </Form.Group>
          <Form.Group controlId="formGroupLName">
            <Form.Label>Last Name</Form.Label>
            <Form.Control type="text" placeholder="Last Name" />
          </Form.Group>
          <Form.Group controlId="formGroupEmail">
            <Form.Label>Email address</Form.Label>
            <InputGroup>
              <InputGroup.Prepend>
                <InputGroup.Text>@</InputGroup.Text>
              </InputGroup.Prepend>
              &nbsp;
              <FormControl
                // id="inlineFormInputGroupUsername"
                placeholder="Username"
              />
            </InputGroup>
          </Form.Group>

          <Form.Group controlId="formGroupEvent">
            <Form.Label>Event:</Form.Label>

            {["radio"].map((type) => (
              <div key={`inline-${type}`} className="mb-3">
                <Form.Check
                  inline
                  label="Single Event"
                  type={type}
                  id={`inline-${type}-1`}
                  name="eventTypeGrp"
                  defaultChecked
                />
                <Form.Check
                  inline
                  label="Multiple Event"
                  type={type}
                  id={`inline-${type}-2`}
                  name="eventTypeGrp"
                  onChange={() => alert("Multiple event Selected")}
                />
              </div>
            ))}

            <div className="row">
              <div className="col-md-5">
                <Form.Control
                  as="select"
                  defaultValue="Select Event"
                  onChange={(e) => {
                    e.target.value === "Other"
                      ? setSingleEvent(true)
                      : setSingleEvent(false);
                  }}
                >
                  <option disabled>Select Event</option>
                  {eventOptions.map((option, index) => {
                    return (
                      <option key={index} value={option.value}>
                        {option.value}
                      </option>
                    );
                  })}
                </Form.Control>
              </div>
              <div className="col-md-7">
                <FormControl
                  disabled={isSingleEvent == false}
                  // id="inlineFormInputGroupUsername"
                  placeholder="Other Event "
                />
              </div>
            </div>
          </Form.Group>
          <div className="row">
            <div className="col-md-6 ">
              <Form.Group controlId="formGroupDate">
                <Form.Label>Event Start Date &nbsp;</Form.Label>
                <DatePicker
                  className="text-muted"
                  // isClearable
                  closeOnScroll={true}
                  closeOnScroll={(e) => e.target === document}
                  // customInput={<ExampleCustomInput />}
                  selected={startDate}
                  onChange={(date) => setStartDate(date)}
                />
              </Form.Group>
            </div>
            <div className="col-md-6 ">
              <Form.Group controlId="formGroupDate">
                <Form.Label>Event End Date &nbsp;</Form.Label>
                <DatePicker
                  className="text-muted"
                  // isClearable
                  closeOnScroll={true}
                  closeOnScroll={(e) => e.target === document}
                  // customInput={<ExampleCustomInput />}
                  selected={endDate}
                  onChange={(date) => setEndDate(date)}
                />
              </Form.Group>
            </div>
          </div>
          <Form.Group>
            <div className="row">
              <div className="col">
                <Form.Control as="select" defaultValue="Choose Country">
                  <option disabled>Choose Country</option>
                  <option>India</option>
                </Form.Control>
                &nbsp;
              </div>
              <div className="col">
                <Form.Control
                  as="select"
                  value={region}
                  onChange={(e) => {
                    setRegion(e.target.value);
                    if(!zip){
                      setZip();
                    }
                    
                  }}
                >
                  <option disabled>Choose State</option>
                  {stateOptions.map((option, index) => {
                    return (
                      <option key={index} value={option}>
                        {option}
                      </option>
                    );
                  })}
                </Form.Control>
              </div>
              <div className="col">
                <Form.Control
                  type="text"
                  placeholder="Zip Code"
                  onChange={fetchZipCode}
                  // value={zip}
                />
              </div>
            </div>
          </Form.Group>
          <Form.Group>
            <Form.Control as="select">
              <option>Event Style</option>
              <option>Classic</option>
              <option>Industrial</option>
              <option>Vintage</option>
              <option>Miscellaneous</option>
            </Form.Control>
          </Form.Group>
          <Form.Group>
            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Form.Group>
        </Form>
      </div>
    </div>
  );
};

export default ContactUs;
